// Helper Functions - Funciones reutilizables para el workflow
try {
  const item = $input.first();

  // Función para limpiar números (remover separadores de miles, convertir comas a puntos)
  function limpiarNumero(str) {
    if (!str) return null;
    return parseFloat(str.replace(/[,]/g, '.').replace(/[^\d.]/g, ''));
  }

  // Función para normalizar nombres (remover tildes, espacios extra)
  function normalizarNombre(nombre) {
    if (!nombre) return null;
    return nombre.trim()
      .replace(/[áàäâ]/gi, 'a')
      .replace(/[éèëê]/gi, 'e')
      .replace(/[íìïî]/gi, 'i')
      .replace(/[óòöô]/gi, 'o')
      .replace(/[úùüû]/gi, 'u')
      .replace(/[ñ]/gi, 'n')
      .replace(/\s+/g, ' ');
  }

  // Función para generar idempotency key
  function generateIdempotencyKey(messageId, chatId, timestamp) {
    const baseString = `${messageId}-${chatId}-${timestamp}`;
    let hash = 0;
    for (let i = 0; i < baseString.length; i++) {
      const char = baseString.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash; // Convert to 32bit integer
    }
    return Math.abs(hash).toString(16);
  }

  // Función para validar estructura básica de WhatsApp - CORREGIDA PARA ESTRUCTURA REAL
  function validateWhatsAppStructure(data) {
    // Los datos vienen con estructura completa del webhook
    if (!data || !data.body) return false;

    const body = data.body;

    // Verificar campos esenciales del webhook
    if (!body.server_url || !body.apikey) return false;
    if (!body.data || !body.data.key) return false;
    if (!body.data.key.remoteJid) return false;
    if (!body.data.message || !body.data.message.conversation) return false;

    return true;
  }

  // Rate limiting simple (usando memoria del nodo)
  function checkRateLimit(chatId) {
    const now = Date.now();
    const RATE_LIMIT_WINDOW = 60000; // 1 minuto
    const MAX_REQUESTS = 10; // máximo 10 mensajes por minuto

    // Usar variables globales de n8n para persistencia temporal
    if (!global.rateLimitCache) {
      global.rateLimitCache = {};
    }

    if (!global.rateLimitCache[chatId]) {
      global.rateLimitCache[chatId] = [];
    }

    // Limpiar requests antiguos
    global.rateLimitCache[chatId] = global.rateLimitCache[chatId].filter(
      timestamp => now - timestamp < RATE_LIMIT_WINDOW
    );

    // Verificar límite
    if (global.rateLimitCache[chatId].length >= MAX_REQUESTS) {
      return false;
    }

    // Agregar request actual
    global.rateLimitCache[chatId].push(now);
    return true;
  }

  // Normalizar datos de entrada - CORREGIDA PARA ESTRUCTURA REAL
  function normalizeWebhookData(rawData) {
    // Los datos ya vienen con la estructura correcta del webhook
    if (rawData.body) {
      // Ya tiene la estructura correcta
      return rawData;
    }

    // Si no tiene .body, crear la estructura esperada
    return {
      body: {
        server_url: rawData.server_url,
        instance: rawData.instance || rawData.instanceId,
        apikey: rawData.apikey,
        data: {
          key: {
            id: rawData.messageTimestamp?.toString() || 'unknown',
            remoteJid: rawData.remoteJid || rawData.sender
          },
          message: {
            conversation: rawData.conversation
          }
        },
        pushName: rawData.pushName
      }
    };
  }

  // Retornar funciones helper disponibles para otros nodos
  return [{
    json: {
      helpers: {
        limpiarNumero: limpiarNumero,
        normalizarNombre: normalizarNombre,
        generateIdempotencyKey: generateIdempotencyKey,
        validateWhatsAppStructure: validateWhatsAppStructure,
        checkRateLimit: checkRateLimit,
        normalizeWebhookData: normalizeWebhookData
      },
      // Pasar datos originales
      originalData: item.json,
      timestamp: DateTime.now().setZone('America/Asuncion').toISO()
    }
  }];
} catch (error) {
  return [{
    json: {
      error: true,
      mensaje: `Error en Helper Functions: ${error.message}`,
      originalData: $input.first().json
    }
  }];
}

// Validación de Seguridad y Estructura - CORREGIDA
try {
  const item = $input.first();
  const rawData = item.json.originalData;
  const helpers = item.json.helpers;

  // 1. Normalizar datos de entrada
  const normalizedData = helpers.normalizeWebhookData(rawData);

  // 2. Validar estructura básica de WhatsApp
  if (!helpers.validateWhatsAppStructure(rawData)) {
    return [{
      json: {
        error: true,
        mensaje: 'Estructura de datos de WhatsApp inválida',
        securityCheck: 'FAILED_STRUCTURE'
      }
    }];
  }

  // 3. Extraer chatId para rate limiting - CORREGIDO
  const actualData = rawData.body || rawData;
  const chatId = actualData.remoteJid || actualData.sender || 'unknown';

  // 4. Verificar rate limiting
  if (!helpers.checkRateLimit(chatId)) {
    return [{
      json: {
        error: true,
        mensaje: 'Demasiados mensajes. Espere un momento antes de enviar otro.',
        securityCheck: 'FAILED_RATE_LIMIT',
        chatId: chatId
      }
    }];
  }

  // 5. Validación de esquema JSON básico - CORREGIDA
  const requiredFields = ['server_url', 'apikey'];
  for (const field of requiredFields) {
    if (!actualData[field]) {
      return [{
        json: {
          error: true,
          mensaje: `Campo requerido faltante: ${field}`,
          securityCheck: 'FAILED_SCHEMA'
        }
      }];
    }
  }

  // 6. Logging de seguridad
  console.log(`[SECURITY] Validación exitosa para chat: ${chatId}`);

  // Si todas las validaciones pasan, retornar datos normalizados
  return [{
    json: {
      ...normalizedData,
      securityCheck: 'PASSED',
      validatedAt: DateTime.now().setZone('America/Asuncion').toISO(),
      helpers: helpers // Pasar helpers a nodos siguientes
    }
  }];

} catch (error) {
  return [{
    json: {
      error: true,
      mensaje: `Error en validación de seguridad: ${error.message}`,
      securityCheck: 'ERROR'
    }
  }];
}

// Procesar mensaje #TASA
try {
  const item = $input.first();
  const texto = item.json.content;
  const chatId = item.json.chatId;
  const messageId = item.json.messageId;

  // Extraer valor de tasa usando regex
  const tasaMatch = texto.match(/#TASA\s+(\d+(?:\.\d+)?)/i);

  if (!tasaMatch) {
    return [{
      json: {
        error: true,
        mensaje: 'Formato de tasa inválido. Use: #TASA 7300'
      }
    }];
  }

  const tasa = parseFloat(tasaMatch[1]);

  if (tasa <= 0) {
    return [{
      json: {
        error: true,
        mensaje: 'La tasa debe ser mayor a 0'
      }
    }];
  }

  // Logging estructurado
  console.log(`[TASA] Procesada tasa: ${tasa} para chat: ${chatId}`);

  return [{
    json: {
      tipo: 'TASA',
      tasa: tasa,
      chat_id: chatId,
      message_id: messageId,
      timestamp: DateTime.now().setZone('America/Asuncion').toISO(),
      audit: {
        action: 'TASA_PROCESSED',
        user: chatId,
        timestamp: DateTime.now().setZone('America/Asuncion').toISO()
      }
    }
  }];
} catch (error) {
  return [{
    json: {
      error: true,
      mensaje: `Error procesando tasa: ${error.message}`
    }
  }];
}

